/* In the vector x negate the odd values and divide the even values by 2.
   Example:

   input: [16, 11, 12, 14, 1, 0, 5]
   output: [8, -11, 6, 7, -1, 0, -5]
*/
void negateOddsAndHalveEvens(std::vector<int> &x) {

outputs:
    std::transform(x.begin(), x.end(), x.begin(), [](int i) {
        if (i % 2 == 0) {
            return i / 2;
        } else {
            return -i;
        }
    });
}

    std::vector<int> nonZeroElements;
    for (int num : x) {
        if (num != 0) {
            nonZeroElements.push_back(num);
        }
    }

    std::sort(nonZeroElements.begin(), nonZeroElements.end());

    size_t nonZeroIndex = 0;
    for (int i = 0; i < x.size(); i += 1) {
        if (x[i] != 0) {
            x[i] = nonZeroElements[nonZeroIndex];
            nonZeroIndex += 1;
        }
    }
}
