	double x, y;
};

double triangleArea(Point const& A, Point const& B, Point const& C) {
  return 0.5 * std::abs( A.x*(B.y-C.y) + B.x*(C.y-A.y) + C.x*(A.y-B.y) );
}

/* Return the area of the smallest triangle that can be formed by any 3 points.
   Use Kokkos to compute in parallel. Assume Kokkos has already been initialized.
   Example:

   input: [{0, 10}, {5, 5}, {1,0}, {-1, 1}, {-10, 0}]
   output: 5.5
*/
double smallestArea(Kokkos::View<const Point*> &points) {

outputs:
double smallestArea = 1e100;
	Kokkos::parallel_reduce(points.extent(0), KOKKOS_LAMBDA(int i, double &update) {
		for (int j = i + 1; j < points.extent(0); ++j) {
			for (int k = j + 1; k < points.extent(0); ++k) {
				double area = triangleArea(points(i), points(j), points(k));
				if (area < update) {
					update = area;
				}
			}
		}
	}, Kokkos::Min<double>(smallestArea));
	return smallestArea;
}

double min_area = 1e100;

	Kokkos::parallel_reduce(points.size(), KOKKOS_LAMBDA(int i, double &update) {
		for (int j = i + 1; j < points.size(); ++j) {
			for (int k = j + 1; k < points.size(); ++k) {
				double area = triangleArea(points[i], points[j], points[k]);
				update = std::min(update, area);
			}
		}
	}, Kokkos::Min<double>(min_area));

	return min_area;
}

