            [0, 0, 1, 0, 0]]
*/
void gameOfLife(std::vector<int> const& input, std::vector<int> &output, size_t N) {
std::vector<int> next(input.size(), 0);
  for (size_t i = 0; i < N; ++i) {
    for (size_t j = 0; j < N; ++j) {
      int count = 0;
      for (int x = -1; x <= 1; ++x) {
        for (int y = -1; y <= 1; ++y) {
          if (x == 0 && y == 0) {
            continue;
          }
          size_t r = (i + x + N) % N;
          size_t c = (j + y + N) % N;
          count += input[r * N + c];
        }
      }
      if (input[i * N + j] == 1) {
        next[i * N + j] = (count == 2 || count == 3);
      } else {
        next[i * N + j] = (count == 3);
      }
    }
  }
  output = next;
}

