  int remainder = x.size() % size;

  std::vector<double> local_x;
  if (rank == 0) {
    for (int i = 1; i < size; ++i) {
      MPI_Send(x.data() + i * chunk_size, chunk_size, MPI_DOUBLE, i, 0, MPI_COMM_WORLD);
    }
    local_x = std::vector<double>(x.begin(), x.begin() + chunk_size + remainder);
  } else {
    MPI_Status status;
    MPI_Recv(local_x.data(), chunk_size, MPI_DOUBLE, 0, 0, MPI_COMM_WORLD, &status);
  }

  double min_distance = std::numeric_limits<double>::max();
