    for (int i = 1; i < size; ++i) {
      MPI_Status status;
      int local_min_index;
      MPI_Recv(&local_min_index, 1, MPI_INT, i, 0, MPI_COMM_WORLD, &status);
      if (local_min_index < min_index) {
        min_index = local_min_index;
      }
    }
    global_min_index = min_index;
  } else {
    MPI_Send(&min_index, 1, MPI_INT, 0, 0, MPI_COMM_WORLD);
  }

  return global_min_index;
