int rank, size;
    MPI_Comm_rank(MPI_COMM_WORLD, &rank);
    MPI_Comm_size(MPI_COMM_WORLD, &size);

    // Split A into M/size rows
    std::vector<double> A_local(M/size*N);
    MPI_Scatter(A.data(), M/size*N, MPI_DOUBLE, A_local.data(), M/size*N, MPI_DOUBLE, 0, MPI_COMM_WORLD);

    // Split x into N/size elements
    std::vector<double> x_local(N/size);
    MPI_Scatter(x.data(), N/size, MPI_DOUBLE, x_local.data(), N/size, MPI_DOUBLE, 0, MPI_COMM_WORLD);

    // Compute the local result
    std::vector<double> y_local(M/size);
    for (size_t i = 0; i < M/size; ++i) {
        double sum = 0;
        for (size_t j = 0; j < N/size; ++j) {
            sum += A_local[i * N/size + j] * x_local[j];
        }
        y_local[i] = sum;
    }

    // Gather the results
    std::vector<double> y_all(M);
    MPI_Gather(y_local.data(), M/size, MPI_DOUBLE, y_all.data(), M/size, MPI_DOUBLE, 0, MPI_COMM_WORLD);

    // Copy the result to y
    if (rank == 0) {
        for (size_t i = 0; i < M; ++i) {
            y[i] = y_all[i];
        }
    }
}

