    // TODO: Your code here
    // 1. Divide the rows of A among the processes
    // 2. Each process computes the dot product of its rows with x
    // 3. Process 0 collects the results from the other processes
    // 4. Process 0 stores the results in y

    // 1. Divide the rows of A among the processes
    int rows_per_process = M / size;
    int remainder = M % size;
    int start_row = rank * rows_per_process;
    int end_row = (rank + 1) * rows_per_process;
    if (rank == size - 1) {
        end_row += remainder;
    }

    // 2. Each process computes the dot product of its rows with x
    std::vector<double> local_y(rows_per_process);
    for (int i = 0; i < rows_per_process; i++) {
        double sum = 0;
        for (int j = 0; j < N; j++) {
            sum += A[start_row * N + j] * x[j];
        }
        local_y[i] = sum;
        start_row++;
    }

    // 3. Process 0 collects the results from the other processes
    if (rank == 0) {
        for (int i = 1; i < size; i++) {
            MPI_Recv(&local_y[0], rows_per_process, MPI_DOUBLE, i, 0, MPI_COMM_WORLD, MPI_STATUS_IGNORE);
        }
    } else {
        MPI_Send(&local_y[0], rows_per_process, MPI_DOUBLE, 0, 0, MPI_COMM_WORLD);
    }

    // 4. Process 0 stores the results in y
    if (rank == 0) {
        for (int i = 0; i < rows_per_process; i++) {
            y[i] = local_y[i];
        }
    }
}

