// Kokkos implementation of xorContains
#include <Kokkos_Core.hpp>

/* Return true if `val` is only in one of vectors x or y.
   Return false if it is in both or neither.
   Use Kokkos to search in parallel. Assume Kokkos has already been initialized.
   Examples:

   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=7
   output: true

   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=1
   output: false
*/
bool xorContains(Kokkos::View<const int*> const& x, Kokkos::View<const int*> const& y, int val) {

translation_src_example:
/* Set `found` to true if `val` is only in one of vectors x or y.
   Set it to false if it is in both or neither.
   Use CUDA to search in parallel. The kernel is launched with at least N threads.
   Examples:

   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=7
   output: true

   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=1
   output: false
*/
__global__ void xorContains(const int *x, const int *y, size_t N, int val, bool *found) {
int tid = threadIdx.x;
  int i = blockIdx.x * blockDim.x + tid;
  int found_local = 0;
  while (i < N) {
    found_local ^= (x[i] == val) | (y[i] == val);
    i += blockDim.x * gridDim.x;
  }
  __syncthreads();
  if (tid == 0) {
    *found = found_local;
  }
}

