double closestPair(std::vector<Point> const& points) {
if (points.size() < 2) {
		return 0;
	}
	double min_dist = std::numeric_limits<double>::max();
	for (int i = 0; i < points.size(); ++i) {
		for (int j = i + 1; j < points.size(); ++j) {
			double dist = distance(points[i], points[j]);
			if (dist < min_dist) {
				min_dist = dist;
			}
		}
	}
	return min_dist;
}

