    // 2. Count the number of pixels in each chunk
    // 3. Reduce the counts to rank 0
    // 4. Gather the counts on rank 0
    // 5. Add the counts on rank 0
    // 6. Broadcast the result to all ranks

    // 1. Split the image into chunks of size image.size()/size
    int chunk_size = image.size() / size;
    std::vector<int> chunk(chunk_size);
    for (int i = 0; i < chunk_size; i++) {
        chunk[i] = image[i + rank * chunk_size];
    }

    // 2. Count the number of pixels in each chunk
    std::array<size_t, 256> local_bins;
    for (auto const& pixel : chunk) {
        local_bins[pixel]++;
    }

    // 3. Reduce the counts to rank 0
    std::array<size_t, 256> reduced_bins;
    MPI_Reduce(local_bins.data(), reduced_bins.data(), 256, MPI_UNSIGNED_LONG, MPI_SUM, 0, MPI_COMM_WORLD);

    // 4. Gather the counts on rank 0
    std::array<size_t, 256> gathered_bins;
    MPI_Gather(reduced_bins.data(), 256, MPI_UNSIGNED_LONG, gathered_bins.data(), 256, MPI_UNSIGNED_LONG, 0, MPI_COMM_WORLD);

    // 5. Add the counts on rank 0
    if (rank == 0) {
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < 256; j++) {
                bins[j] += gathered_bins[i * 256 + j];
            }
        }
    }

    // 6. Broadcast the result to all ranks
    MPI_Bcast(bins.data(), 256, MPI_UNSIGNED_LONG, 0, MPI_COMM_WORLD);
