int rank, size;
  MPI_Comm_rank(MPI_COMM_WORLD, &rank);
  MPI_Comm_size(MPI_COMM_WORLD, &size);

  int n = x.size();
  int chunk = n/size;
  int remainder = n%size;

  int start = rank*chunk;
  int end = start + chunk;
  if (rank == size-1) {
    end += remainder;
  }

  for (int i = start; i < end; ++i) {
    z[i] = alpha*x[i] + y[i];
  }

  MPI_Barrier(MPI_COMM_WORLD);

  if (rank == 0) {
    for (int i = 1; i < size; ++i) {
      int start = i*chunk;
      int end = start + chunk;
      if (i == size-1) {
        end += remainder;
      }
      MPI_Recv(&z[start], end-start, MPI_DOUBLE, i, 0, MPI_COMM_WORLD, MPI_STATUS_IGNORE);
    }
  } else {
    MPI_Send(&z[start], end-start, MPI_DOUBLE, 0, 0, MPI_COMM_WORLD);
  }
}

