*/
void ifft(std::vector<std::complex<double>> &x) {

// Take conjugate of the complex numbers
for (auto &elem : x) {
    elem = std::conj(elem);
}

// Apply fft
fft(x);

// Take conjugate again
for (auto &elem : x) {
    elem = std::conj(elem);
}

// Scale the numbers
for (auto &elem : x) {
    elem /= static_cast<double>(x.size());
}
}

