   output: 3
*/
int maxDegree(std::vector<int> const& A, size_t N) {
int max_degree = 0;
  for (size_t i = 0; i < N; ++i) {
    int degree = 0;
    for (size_t j = 0; j < N; ++j) {
      if (A[i * N + j] == 1) {
        ++degree;
      }
    }
    if (degree > max_degree) {
      max_degree = degree;
    }
  }
  return max_degree;
}

