         for (size_t j = 0; j < i; ++j) {
            sum += L[k * N + j] * U[j * N + i];
         }
         L[k * N + i] = (A_map[{k, i}] - sum) / U[i * N + i];
      }

      // L(i, i) = 1
      L[i * N + i] = 1.0;
