      MPI_Gather(&L[0], N * N, MPI_DOUBLE, &L_all[0], N * N, MPI_DOUBLE, 0, MPI_COMM_WORLD);
      MPI_Gather(&U[0], N * N, MPI_DOUBLE, &U_all[0], N * N, MPI_DOUBLE, 0, MPI_COMM_WORLD);

