   output: [{4,0}, {1,-2.41421}, {0,0}, {1,-0.414214}, {0,0}, {1,0.414214}, {0,0}, {1,2.41421}]
*/
void fftConjugate(std::vector<std::complex<double>> &x) {
int n = x.size();
    int m = 0;
    for (int i = 0; i < n; i++) {
        int j = i;
        for (int k = 0; k < m; k++) {
            j = j ^ (1 << k);
