// Kokkos implementation of average
#include <Kokkos_Core.hpp>

/* Return the average of the vector x.
   Use Kokkos to compute in parallel. Assume Kokkos has already been initialized.
   Examples:

	 input: [1, 8, 4, 5, 1]
   output: 3.8

   input: [2, 2, 2, 3]
   output: 2.25
*/
double average(Kokkos::View<const double*> const& x) {

translation_src_example:
/* Compute the average of the vector x. Store the result in average.
   Use CUDA to compute in parallel. The kernel is launched with at least as many threads as values in x.
   Examples:

	 input: [1, 8, 4, 5, 1]
   output: 3.8

   input: [2, 2, 2, 3]
   output: 2.25
*/
__global__ void average(const double *x, size_t N, double *average) {

    int tid = blockIdx.x * blockDim.x + threadIdx.x;
    double sum = 0.0;
    if (tid < N) {
        sum = x[tid];
    }
    sum = atomicAdd(average, sum);
    __syncthreads();
    if (tid == 0) {
        *average /= N;
    }
}

