void fftConjugate(std::vector<std::complex<double>> &x) {
int n = x.size();
    int m = 0;
    for (int i = 0; i < n; i++) {
        int j = i;
        for (int k = 0; k < m; k++) {
            j = j ^ (1 << k);
        }
        if (i < j) {
            std::complex<double> temp = x[i];
            x[i] = x[j];
