    int block_size = (n + size - 1) / size;
    std::vector<double> local_x(block_size);
    std::vector<std::complex<double>> local_output(block_size);

    MPI_Scatter(x.data(), block_size, MPI_DOUBLE, local_x.data(), block_size, MPI_DOUBLE, 0, MPI_COMM_WORLD);

