  MPI_Scatter(input.data(), local_size * N, MPI_INT, local_input.data(), local_size * N, MPI_INT, 0, MPI_COMM_WORLD);

  // Compute local part of the game
  for (size_t i = 0; i < local_size; ++i) {
    for (size_t j = 0; j < N; ++j) {
      int count = 0;
      for (int x = -1; x <= 1; ++x) {
        for (int y = -1; y <= 1; ++y) {
