        local_output[i * N + j] = (count == 2 || count == 3);
      } else {
        local_output[i * N + j] = (count == 3);
      }
    }
  }

  // Gather local results to rank 0
  MPI_Gather(local_output.data(), local_size * N, MPI_INT, output.data(), local_size * N, MPI_INT, 0, MPI_COMM_WORLD);

