   output: [3, 1, 0, 2]
*/
__global__ void countQuadrants(const Point *points, size_t N, size_t bins[4]) {
int i = threadIdx.x;
    if (i < N) {
        double x = points[i].x;
        double y = points[i].y;
        if (x >= 0 && y >= 0) {
            atomicAdd(&bins[0], 1);
        } else if (x < 0 && y >= 0) {
            atomicAdd(&bins[1], 1);
        } else if (x < 0 && y < 0) {
            atomicAdd(&bins[2], 1);
        } else if (x >= 0 && y < 0) {
            atomicAdd(&bins[3], 1);
        }
    }
}

