#include <Kokkos_Core.hpp>

/* Count the number of doubles in the vector x that have a fractional part 
   in [0, 0.25), [0.25, 0.5), [0.5, 0.75), and [0.75, 1). Store the counts in `bins`.
   Use Kokkos to compute in parallel. Assume that Kokkos has already been initialized.
   Examples:

   input: [7.8, 4.2, 9.1, 7.6, 0.27, 1.5, 3.8]
   output: [2, 1, 2, 2]

   input: [1.9, 0.2, 0.6, 10.1, 7.4]
   output: [2, 1, 1, 1]
*/
void countQuartiles(Kokkos::View<const double*> const& x, Kokkos::View<size_t[4]> &bins) {

translation_src_example:
/* Count the number of doubles in the vector x that have a fractional part 
   in [0, 0.25), [0.25, 0.5), [0.5, 0.75), and [0.75, 1). Store the counts in `bins`.
   Use CUDA to compute in parallel. The kernel is launched with at least N threads.
   Examples:

   input: [7.8, 4.2, 9.1, 7.6, 0.27, 1.5, 3.8]
   output: [2, 1, 2, 2]

   input: [1.9, 0.2, 0.6, 10.1, 7.4]
   output: [2, 1, 1, 1]
*/
__global__ void countQuartiles(const double *x, size_t N, size_t bins[4]) {
size_t i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i < N) {
        double frac = fmod(x[i], 1.0);
        if (frac < 0.25)
            atomicAdd(&bins[0], 1);
        else if (frac < 0.5)
            atomicAdd(&bins[1], 1);
        else if (frac < 0.75)
            atomicAdd(&bins[2], 1);
        else
            atomicAdd(&bins[3], 1);
    }
}

