// Kokkos implementation of dft
#include <Kokkos_Core.hpp>

/* Compute the discrete fourier transform of x. Store the result in output.
   Use Kokkos to compute in parallel. Assume Kokkos is already initialized.
   Example:

   input: [1, 4, 9, 16]
   output: [30+0i, -8-12i, -10-0i, -8+12i]
*/
void dft(Kokkos::View<const double*> &x, Kokkos::View<Kokkos::complex<double>*> &output) {

translation_src_example:
/* Compute the discrete fourier transform of x. Store the result in output.
   Use CUDA to compute in parallel. The kernel is launched with at least N threads.
   Example:

   input: [1, 4, 9, 16]
   output: [30+0i, -8-12i, -10-0i, -8+12i]
*/
__global__ void dft(const double *x, cuDoubleComplex *output, size_t N) {
int tid = blockIdx.x * blockDim.x + threadIdx.x;
    if (tid >= N) return;

    cuDoubleComplex sum = {0, 0};
    for (size_t k = 0; k < N; ++k) {
        double angle = -2 * M_PI * tid * k / N;
        cuDoubleComplex exp_part = {cos(angle), sin(angle)};
        cuDoubleComplex term = cuCmul(exp_part, make_cuDoubleComplex(x[k], 0));
        sum = cuCadd(sum, term);
    }
    output[tid] = sum;
}

