// Kokkos implementation of ranks
#include <Kokkos_Core.hpp>

/* For each value in the array x compute its index in the sorted vector.
   Store the results in `ranks`.
   Use Kokkos to compute in parallel. Assume Kokkos has already been initialized.
   Examples:

   input: [3.1, 2.8, 9.1, 0.4, 3.14]
   output: [2, 1, 4, 0, 3]

   input: [100, 7.6, 16.1, 18, 7.6]
   output: [4, 0, 1, 2, 3]
*/
void ranks(Kokkos::View<const float*> const& x, Kokkos::View<size_t*> &ranks) {

translation_src_example:
/* For each value in the vector x compute its index in the sorted vector.
   Store the results in `ranks`.
   Use CUDA to compute in parallel. The kernel will be launched with at least as many threads as elements in x.
   Examples:

   input: [3.1, 2.8, 9.1, 0.4, 3.14]
   output: [2, 1, 4, 0, 3]

   input: [100, 7.6, 16.1, 18, 7.6]
   output: [4, 0, 1, 2, 3]
*/
__global__ void ranks(const float *x, size_t *ranks, size_t N) {
size_t idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx < N) {
        float val = x[idx];
        size_t rank = 0;
        for (size_t i = 0; i < N; ++i) {
            if (x[i] <= val) {
                ++rank;
            }
        }
        ranks[idx] = rank - 1;
    }
}

