__global__ void jacobi2D(const double *input, double *output, size_t N) {
int i = blockIdx.x * blockDim.x + threadIdx.x;
    int j = blockIdx.y * blockDim.y + threadIdx.y;
    if (i >= N || j >= N) {
        return;
    }
    int idx = i + j * N;
