int rank, size;
   MPI_Comm_rank(MPI_COMM_WORLD, &rank);
   MPI_Comm_size(MPI_COMM_WORLD, &size);

   // Compute the number of rows and columns in each partition
   size_t M_per_rank = M / size;
   size_t K_per_rank = K / size;
   size_t M_remainder = M % size;
   size_t K_remainder = K % size;

   // Compute the start and end rows and columns for each partition
   size_t start_row = rank * M_per_rank;
   size_t end_row = (rank < M_remainder)? (start_row + M_per_rank + 1) : (start_row + M_per_rank + M_remainder);
   size_t start_col = rank * K_per_rank;
   size_t end_col = (rank < K_remainder)? (start_col + K_per_rank + 1) : (start_col + K_per_rank + K_remainder);

   // Compute the number of nonzeros in each partition
   size_t nnz_per_rank = 0;
   for (auto const& a : A) {
      if (a.row >= start_row && a.row < end_row && a.column >= start_col && a.column < end_col) {
         nnz_per_rank++;
      }
   }

   // Compute the number of nonzeros in each partition
   size_t nnz_per_rank_X = 0;
   for (auto const& x : X) {
      if (x.row >= start_row && x.row < end_row && x.column >= start_col && x.column < end_col) {
         nnz_per_rank_X++;
      }
   }

   // Compute the number of nonzeros in each partition
   size_t nnz_per_rank_Y = 0;
   for (auto const& a : A) {
      for (auto const& x : X) {
         if (a.row == x.row && a.column == x.column) {
            nnz_per_rank_Y++;
         }
      }
   }

   // Allocate space for the nonzero elements of A and X in each partition
   std::vector<COOElement> A_per_rank(nnz_per_rank);
   std::vector<COOElement> X_per_rank(nnz_per_rank_X);
   std::vector<double> Y_per_rank(nnz_per_rank_Y);

   // Copy the nonzero elements of A and X in each partition
   size_t i = 0;
   for (auto const& a : A) {
      if (a.row >= start_row && a.row < end_row && a.column >= start_col && a.column < end_col) {
         A_per_rank[i] = a;
         i++;
      }
   }
   i = 0;
   for (auto const& x : X) {
      if (x.row >= start_row && x.row < end_row && x.column >= start_col && x.column < end_col) {
         X_per_rank[i] = x;
         i++;
      }
   }

   // Compute the matrix multiplication Y=AX in each partition
   spmm(A_per_rank, X_per_rank, Y_per_rank, M_per_rank, K_per_rank, N);

   // Gather the results from each partition
   std::vector<double> Y_gathered(nnz_per_rank_Y);
