   output: [2, 1, 1, 1]
*/
void countQuartiles(std::vector<double> const& x, std::array<size_t, 4> &bins) {
for (double num : x) {
        double fractional_part = num - std::floor(num);
        if (fractional_part < 0.25) {
            bins[0]++;
        } else if (fractional_part < 0.5) {
            bins[1]++;
        } else if (fractional_part < 0.75) {
            bins[2]++;
        } else {
            bins[3]++;
        }
    }
}

