    Kokkos::TeamPolicy<Kokkos::DefaultExecutionSpace> policy(N, N);

    // Launch the kernel
    Kokkos::parallel_for("shortestPathLength", policy, KOKKOS_LAMBDA(const Kokkos::TeamPolicy<Kokkos::DefaultExecutionSpace>::member_type &teamMember) {
        int row = teamMember.league_rank();
        int col = teamMember.team_rank();

        if (row < N && col < N) {
            if (row == col) {
                pathLength(row) = 0;
            } else if (A(row, col) == 1) {
                pathLength(row) = 1;
            } else {
                pathLength(row) = INT_MAX;
            }

            teamMember.team_barrier();

            for (int k = 0; k < N; ++k) {
                if (pathLength(row) + pathLength(k) < pathLength(col)) {
                    pathLength(col) = pathLength(row) + pathLength(k);
                }
                teamMember.team_barrier();
            }
