int smallestOdd(Kokkos::View<const int*> const& x) {

translation_src_example:
/* Find the value of the smallest odd number in the vector x. Store it in smallest.
   Use CUDA to compute in parallel. The kernel is launched with the same number of threads as elements in x.
   Examples:

   input: [7, 9, 5, 2, 8, 16, 4, 1]
   output: 1

   input: [8, 36, 7, 2, 11]
   output: 7
*/
__global__ void smallestOdd(const int *x, size_t N, int *smallest) {
int thread_id = threadIdx.x;
    int block_id = blockIdx.x;
    int stride = blockDim.x;

    int local_smallest = x[block_id * stride + thread_id];
    for (int i = 1; i < stride; i++) {
        int value = x[block_id * stride + thread_id + i];
        if (value % 2 == 1 && value < local_smallest) {
            local_smallest = value;
        }
    }
    if (local_smallest % 2 == 1) {
        atomicMin(smallest, local_smallest);
    }
}

