#include <Kokkos_Core.hpp>

/* Compute the prefix sum of the vector x into output.
   Use Kokkos to compute in parallel. Assume Kokkos is already initialized.
   Example:

   input: [1, 7, 4, 6, 6, 2]
   output: [1, 8, 12, 18, 24, 26]
*/
void prefixSum(Kokkos::View<const double*> const& x, Kokkos::View<double*> &output) {

translation_src_example:
/* Compute the prefix sum of the vector x into output.
   Use CUDA to compute in parallel. The kernel is launched with at least as many threads as elements in x.
   Example:

   input: [1, 7, 4, 6, 6, 2]
   output: [1, 8, 12, 18, 24, 26]
*/
__global__ void prefixSum(const double *x, double *output, size_t N) {
int tid = threadIdx.x + blockIdx.x * blockDim.x;
    int stride = blockDim.x * gridDim.x;

    for (size_t i = tid; i < N; i += stride) {
        double sum = 0;
        for (size_t j = 0; j <= i; ++j) {
            sum += x[j];
        }
        output[i] = sum;
    }
}

