   output: 2
*/
__global__ void findLastShortBook(const Book *books, size_t N, size_t *lastShortBookIndex) {

// Thread index
int i = blockIdx.x * blockDim.x + threadIdx.x;

// Check if the thread index is within the bounds of the books array
if (i < N) {
    // Check if the book has less than 100 pages
    if (books[i].pages < 100) {
        // Use atomicMax to safely find the largest index among all threads
        atomicMax((int*)lastShortBookIndex, i);
